"use client"

import type React from "react"

import { useState } from "react"
import { useRouter } from "next/navigation"
import Link from "next/link"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Separator } from "@/components/ui/separator"
import { Alert, AlertDescription } from "@/components/ui/alert"
import { Mail, Lock, Chrome, Loader2, AlertTriangle } from "lucide-react"
import { useToast } from "@/hooks/use-toast"
import { useAuth } from "@/contexts/auth-context"

export function LoginForm() {
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [isLoading, setIsLoading] = useState(false)
  const [isGoogleLoading, setIsGoogleLoading] = useState(false)
  const router = useRouter()
  const { toast } = useToast()
  const { firebaseConfigured } = useAuth()

  const handleEmailLogin = async (e: React.FormEvent) => {
    e.preventDefault()

    if (!firebaseConfigured) {
      toast({
        title: "Configuration Required",
        description: "Firebase authentication is not configured. Please check your environment variables.",
        variant: "destructive",
      })
      return
    }

    setIsLoading(true)

    try {
      const { signInWithEmail } = await import("@/lib/auth")
      await signInWithEmail(email, password)
      toast({
        title: "Welcome back!",
        description: "You've been successfully signed in.",
      })
      router.push("/dashboard")
    } catch (error: any) {
      toast({
        title: "Sign in failed",
        description: error.message,
        variant: "destructive",
      })
    } finally {
      setIsLoading(false)
    }
  }

  const handleGoogleLogin = async () => {
    if (!firebaseConfigured) {
      toast({
        title: "Configuration Required",
        description: "Firebase authentication is not configured. Please check your environment variables.",
        variant: "destructive",
      })
      return
    }

    setIsGoogleLoading(true)

    try {
      const { signInWithGoogle } = await import("@/lib/auth")
      await signInWithGoogle()
      toast({
        title: "Welcome back!",
        description: "You've been successfully signed in with Google.",
      })
      router.push("/dashboard")
    } catch (error: any) {
      toast({
        title: "Google sign in failed",
        description: error.message,
        variant: "destructive",
      })
    } finally {
      setIsGoogleLoading(false)
    }
  }

  if (!firebaseConfigured) {
    return (
      <div className="space-y-6">
        <Alert className="border-destructive/20 bg-destructive/5">
          <AlertTriangle className="h-4 w-4 text-destructive" />
          <AlertDescription className="text-destructive">
            <strong>Firebase Configuration Required</strong>
            <br />
            Authentication is currently disabled because Firebase environment variables are not configured.
            <br />
            <span className="text-sm">Please set up your Firebase project in Project Settings.</span>
          </AlertDescription>
        </Alert>

        <div className="text-center">
          <Link href="/" className="text-primary hover:underline">
            â† Back to Home
          </Link>
        </div>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      <Button
        onClick={handleGoogleLogin}
        disabled={isGoogleLoading}
        variant="outline"
        className="w-full h-12 text-base bg-transparent"
      >
        {isGoogleLoading ? <Loader2 className="w-5 h-5 animate-spin mr-2" /> : <Chrome className="w-5 h-5 mr-2" />}
        Continue with Google
      </Button>

      <div className="relative">
        <div className="absolute inset-0 flex items-center">
          <Separator className="w-full" />
        </div>
        <div className="relative flex justify-center text-xs uppercase">
          <span className="bg-card px-2 text-muted-foreground">Or continue with email</span>
        </div>
      </div>

      <form onSubmit={handleEmailLogin} className="space-y-4">
        <div className="space-y-2">
          <Label htmlFor="email">Email</Label>
          <div className="relative">
            <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-muted-foreground" />
            <Input
              id="email"
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="pl-10 h-12"
              required
            />
          </div>
        </div>

        <div className="space-y-2">
          <Label htmlFor="password">Password</Label>
          <div className="relative">
            <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-muted-foreground" />
            <Input
              id="password"
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="pl-10 h-12"
              required
            />
          </div>
        </div>

        <Button type="submit" disabled={isLoading} className="w-full h-12 text-base font-medium">
          {isLoading ? <Loader2 className="w-5 h-5 animate-spin mr-2" /> : null}
          Sign In
        </Button>
      </form>

      <div className="text-center text-sm">
        <span className="text-muted-foreground">{"Don't have an account? "}</span>
        <Link href="/register" className="text-primary hover:underline font-medium">
          Sign up
        </Link>
      </div>
    </div>
  )
}

components/auth/register-form.tsx.content:
"use client"

import type React from "react"

import { useState } from "react"
import { useRouter } from "next/navigation"
import Link from "next/link"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Separator } from "@/components/ui/separator"
import { signUpWithEmail, signInWithGoogle } from "@/lib/auth"
import { User, Mail, Lock, Chrome, Loader2 } from "lucide-react"
import { useToast } from "@/hooks/use-toast"

export function RegisterForm() {
  const [name, setName] = useState("")
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [confirmPassword, setConfirmPassword] = useState("")
  const [isLoading, setIsLoading] = useState(false)
  const [isGoogleLoading, setIsGoogleLoading] = useState(false)
  const router = useRouter()
  const { toast } = useToast()

  const handleEmailRegister = async (e: React.FormEvent) => {
    e.preventDefault()

    if (password !== confirmPassword) {
      toast({
        title: "Passwords don't match",
        description: "Please make sure your passwords match.",
        variant: "destructive",
      })
      return
    }

    if (password.length < 6) {
      toast({
        title: "Password too short",
        description: "Password must be at least 6 characters long.",
        variant: "destructive",
      })
      return
    }

    setIsLoading(true)

    try {
      await signUpWithEmail(email, password, name)
      toast({
        title: "Account created!",
        description: "Welcome to CropPredict. Your account has been created successfully.",
      })
      router.push("/dashboard")
    } catch (error: any) {
      toast({
        title: "Registration failed",
        description: error.message,
        variant: "destructive",
      })
    } finally {
      setIsLoading(false)
    }
  }

  const handleGoogleRegister = async () => {
    setIsGoogleLoading(true)

    try {
      await signInWithGoogle()
      toast({
        title: "Account created!",
        description: "Welcome to CropPredict. Your account has been created with Google.",
      })
      router.push("/dashboard")
    } catch (error: any) {
      toast({
        title: "Google registration failed",
        description: error.message,
        variant: "destructive",
      })
    } finally {
      setIsGoogleLoading(false)
    }
  }

  return (
    <div className="space-y-6">
      <Button
        onClick={handleGoogleRegister}
        disabled={isGoogleLoading}
        variant="outline"
        className="w-full h-12 text-base bg-transparent"
      >
        {isGoogleLoading ? <Loader2 className="w-5 h-5 animate-spin mr-2" /> : <Chrome className="w-5 h-5 mr-2" />}
        Continue with Google
      </Button>

      <div className="relative">
        <div className="absolute inset-0 flex items-center">
          <Separator className="w-full" />
        </div>
        <div className="relative flex justify-center text-xs uppercase">
          <span className="bg-card px-2 text-muted-foreground">Or create account with email</span>
        </div>
      </div>

      <form onSubmit={handleEmailRegister} className="space-y-4">
        <div className="space-y-2">
          <Label htmlFor="name">Full Name</Label>
          <div className="relative">
            <User className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-muted-foreground" />
            <Input
              id="name"
              type="text"
              placeholder="Enter your full name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="pl-10 h-12"
              required
            />
          </div>
        </div>

        <div className="space-y-2">
          <Label htmlFor="email">Email</Label>
          <div className="relative">
            <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-muted-foreground" />
            <Input
              id="email"
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="pl-10 h-12"
              required
            />
          </div>
        </div>

        <div className="space-y-2">
          <Label htmlFor="password">Password</Label>
          <div className="relative">
            <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-muted-foreground" />
            <Input
              id="password"
              type="password"
              placeholder="Create a password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="pl-10 h-12"
              required
            />
          </div>
        </div>

        <div className="space-y-2">
          <Label htmlFor="confirmPassword">Confirm Password</Label>
          <div className="relative">
            <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-muted-foreground" />
            <Input
              id="confirmPassword"
              type="password"
              placeholder="Confirm your password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              className="pl-10 h-12"
              required
            />
          </div>
        </div>

        <Button type="submit" disabled={isLoading} className="w-full h-12 text-base font-medium">
          {isLoading ? <Loader2 className="w-5 h-5 animate-spin mr-2" /> : null}
          Create Account
        </Button>
      </form>

      <div className="text-center text-sm">
        <span className="text-muted-foreground">Already have an account? </span>
        <Link href="/login" className="text-primary hover:underline font-medium">
          Sign in
        </Link>
      </div>
    </div>
  )
}

components/farms/add-farm-dialog.tsx.content:
"use client"

import type React from "react"

import { useState } from "react"
import { Button } from "@/components/ui/button"
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { Plus, Loader2, MapPin } from "lucide-react"
import { apiClient } from "@/lib/api"
import { useToast } from "@/hooks/use-toast"

interface AddFarmDialogProps {
  onFarmAdded: () => void
}

export function AddFarmDialog({ onFarmAdded }: AddFarmDialogProps) {
  const [open, setOpen] = useState(false)
  const [isLoading, setIsLoading] = useState(false)
  const [isGettingLocation, setIsGettingLocation] = useState(false)
  const [formData, setFormData] = useState({
    name: "",
    latitude: "",
    longitude: "",
    soil_type: "",
    area_ha: "",
  })
  const { toast } = useToast()

  const handleGetLocation = () => {
    if (!navigator.geolocation) {
      toast({
        title: "Geolocation not supported",
        description: "Your browser doesn't support geolocation.",
        variant: "destructive",
      })
      return
    }

    setIsGettingLocation(true)
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setFormData((prev) => ({
          ...prev,
          latitude: position.coords.latitude.toString(),
          longitude: position.coords.longitude.toString(),
        }))
        setIsGettingLocation(false)
        toast({
          title: "Location obtained",
          description: "Your current location has been set for the farm.",
        })
      },
      (error) => {
        setIsGettingLocation(false)
        toast({
          title: "Location error",
          description: "Unable to get your current location. Please enter coordinates manually.",
          variant: "destructive",
        })
      },
    )
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setIsLoading(true)

    try {
      await apiClient.addFarm({
        name: formData.name,
        location: {
          lat: Number.parseFloat(formData.latitude),
          lon: Number.parseFloat(formData.longitude),
        },
        soil_type: formData.soil_type,
        area_ha: Number.parseFloat(formData.area_ha),
      })

      toast({
        title: "Farm added successfully",
        description: `${formData.name} has been added to your farms.`,
      })

      setFormData({
        name: "",
        latitude: "",
        longitude: "",
        soil_type: "",
        area_ha: "",
      })
      setOpen(false)
      onFarmAdded()
    } catch (error: any) {
      toast({
        title: "Failed to add farm",
        description: error.message,
        variant: "destructive",
      })
    } finally {
      setIsLoading(false)
    }
  }

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        <Button>
          <Plus className="w-4 h-4 mr-2" />
          Add Farm
        </Button>
      </DialogTrigger>
      <DialogContent className="sm:max-w-[425px]">
        <DialogHeader>
          <DialogTitle>Add New Farm</DialogTitle>
          <DialogDescription>Add a new farm location to start making yield predictions.</DialogDescription>
        </DialogHeader>
        <form onSubmit={handleSubmit}>
          <div className="grid gap-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="name">Farm Name</Label>
              <Input
                id="name"
                placeholder="e.g., North Field"
                value={formData.name}
                onChange={(e) => setFormData((prev) => ({ ...prev, name: e.target.value }))}
                required
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="latitude">Latitude</Label>
                <Input
                  id="latitude"
                  type="number"
                  step="any"
                  placeholder="e.g., 40.7128"
                  value={formData.latitude}
                  onChange={(e) => setFormData((prev) => ({ ...prev, latitude: e.target.value }))}
                  required
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="longitude">Longitude</Label>
                <Input
                  id="longitude"
                  type="number"
                  step="any"
                  placeholder="e.g., -74.0060"
                  value={formData.longitude}
                  onChange={(e) => setFormData((prev) => ({ ...prev, longitude: e.target.value }))}
                  required
                />
              </div>
            </div>

            <Button
              type="button"
              variant="outline"
              onClick={handleGetLocation}
              disabled={isGettingLocation}
              className="w-full bg-transparent"
            >
              {isGettingLocation ? (
                <Loader2 className="w-4 h-4 mr-2 animate-spin" />
              ) : (
                <MapPin className="w-4 h-4 mr-2" />
              )}
              Use Current Location
            </Button>

            <div className="space-y-2">
              <Label htmlFor="soil_type">Soil Type</Label>
              <Select
                value={formData.soil_type}
                onValueChange={(value) => setFormData((prev) => ({ ...prev, soil_type: value }))}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select soil type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="clay">Clay</SelectItem>
                  <SelectItem value="loam">Loam</SelectItem>
                  <SelectItem value="sand">Sand</SelectItem>
                  <SelectItem value="silt">Silt</SelectItem>
                  <SelectItem value="peat">Peat</SelectItem>
                  <SelectItem value="chalk">Chalk</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label htmlFor="area_ha">Area (Hectares)</Label>
              <Input
                id="area_ha"
                type="number"
                step="0.1"
                placeholder="e.g., 2.5"
                value={formData.area_ha}
                onChange={(e) => setFormData((prev) => ({ ...prev, area_ha: e.target.value }))}
                required
              />
            </div>
          </div>
          <DialogFooter>
            <Button type="button" variant="outline" onClick={() => setOpen(false)}>
              Cancel
            </Button>
            <Button type="submit" disabled={isLoading}>
              {isLoading ? <Loader2 className="w-4 h-4 mr-2 animate-spin" /> : null}
              Add Farm
            </Button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  )
}

components/predictions/prediction-histor.content:
"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import { apiClient } from "@/lib/api"
import { Calendar, TrendingUp, MapPin, Loader2 } from "lucide-react"
import { format } from "date-fns"

export function PredictionHistory() {
  const [predictions, setPredictions] = useState([])
  const [isLoading, setIsLoading] = useState(true)

  useEffect(() => {
    loadPredictions()
  }, [])

  const loadPredictions = async () => {
    try {
      const response = await apiClient.getPredictions()
      setPredictions(response.predictions)
    } catch (error) {
      console.error("Error loading predictions:", error)
    } finally {
      setIsLoading(false)
    }
  }

  if (isLoading) {
    return (
      <Card>
        <CardContent className="flex items-center justify-center py-12">
          <Loader2 className="w-8 h-8 animate-spin" />
        </CardContent>
      </Card>
    )
  }

  if (predictions.length === 0) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>No Predictions Yet</CardTitle>
          <CardDescription>You haven't made any yield predictions yet.</CardDescription>
        </CardHeader>
        <CardContent>
          <Button onClick={() => (window.location.href = "/dashboard/predictions/new")}>Make First Prediction</Button>
        </CardContent>
      </Card>
    )
  }

  return (
    <div className="space-y-4">
      {predictions.map((prediction: any, index: number) => (
        <Card key={index}>
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle className="flex items-center gap-2">
                <MapPin className="w-4 h-4" />
                Farm: {prediction.farm_id}
              </CardTitle>
              <Badge variant={prediction.status === "complete" ? "default" : "secondary"}>{prediction.status}</Badge>
            </div>
            <CardDescription className="flex items-center gap-2">
              <Calendar className="w-4 h-4" />
              {format(new Date(prediction.created_at.seconds * 1000), "PPP")}
            </CardDescription>
          </CardHeader>
          <CardContent>
            {prediction.status === "complete" && prediction.outputs ? (
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <div>
                    <div className="text-2xl font-bold text-primary">
                      {prediction.outputs.predicted_yield_kg_per_ha} kg/ha
                    </div>
                    <div className="text-sm text-muted-foreground">
                      Range: {prediction.outputs.confidence_interval?.lower?.toFixed(1)} -{" "}
                      {prediction.outputs.confidence_interval?.upper?.toFixed(1)} kg/ha
                    </div>
                  </div>
                  <TrendingUp className="w-8 h-8 text-muted-foreground" />
                </div>

                {prediction.inputs && (
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                    <div>
                      <div className="font-medium">Crop</div>
                      <div className="text-muted-foreground">{prediction.inputs.crop}</div>
                    </div>
                    <div>
                      <div className="font-medium">Area</div>
                      <div className="text-muted-foreground">{prediction.inputs.area} ha</div>
                    </div>
                    <div>
                      <div className="font-medium">Fertilizer</div>
                      <div className="text-muted-foreground">{prediction.inputs.fertilizer} kg</div>
                    </div>
                    <div>
                      <div className="font-medium">Pesticide</div>
                      <div className="text-muted-foreground">{prediction.inputs.pesticide} kg</div>
                    </div>
                  </div>
                )}
              </div>
            ) : prediction.status === "error" ? (
              <div className="text-destructive">
                <p>Prediction failed: {prediction.error}</p>
              </div>
            ) : (
              <div className="flex items-center gap-2">
                <Loader2 className="w-4 h-4 animate-spin" />
                <span>Processing prediction...</span>
              </div>
            )}
          </CardContent>
        </Card>
      ))}
    </div>
  )
}

